                    authorName:
                        "\u0905\u0902\u0938\u093e\u0930 \u0915\u092e\u094d\u092c\u0930\u0940",
                    content:
                        "\u0915\u0947\u0935\u0932 \u092a\u0930\u0928\u093f\u0902\u0926\u093e \u0938\u0941\u0928\u0947, \u0928\u0939\u0940\u0902 \u0938\u0941\u0928\u0947 \u0917\u0941\u0923\u0917\u093e\u0928\u0964 \u0926\u0940\u0935\u093e\u0930\u094b\u0902 \u0915\u0947 \u092a\u093e\u0938 \u0939\u0948\u0902, \u091c\u093e\u0928\u0947 \u0915\u0948\u0938\u0947 \u0915\u093e\u0928 \u0964\u0964 \u0938\u0942\u092b\u0940 \u0938\u0902\u0924 \u091a\u0932\u0947 \u0917\u090f, \u0938\u092c \u091c\u0902\u0917\u0932 \u0915\u0940 \u0913\u0930\u0964\n\u092e\u0902\u0926\u093f\u0930 \u092e\u0938\u094d\u091c\u093f\u0926 \u092e\u0947\u0902 \u092e\u093f\u0932\u0947, \u0930\u0902\u0917 \u092c\u093f\u0930\u0902\u0917\u0947 \u091a\u094b\u0930 \u0964\u0964 \u0938\u092b\u0932 \u0935\u0939\u0940 \u0939\u0948 \u0906\u091c\u0915\u0932, \u0935\u0939\u0940 \u0939\u0941\u0906 \u0938\u093f\u0930\u092e\u094c\u0930\u0964 \u091c\u093f\u0938\u0915\u0940 \u0915\u0925\u0928\u0940 \u0914\u0930 \u0939\u0948, \u091c\u093f\u0938\u0915\u0940 \u0915\u0930\u0928\u0940 \u0914\u0930\u0964\u0964 \u0939\u092e\u0915\u094b \u092f\u0939 \u0938\u0941\u0935\u093f\u0927\u093e \u092e\u093f\u0932\u0940, \u092a\u093e\u0930 \u0909\u0924\u0930\u0928\u0947 \u0939\u0947\u0924\u0941\u0964 \u0928\u0926\u093f\u092f\u093e \u0924\u094b \u0939\u0948 \u0906\u0917 \u0915\u0940, \u0914\u0930 \u092e\u094b\u092e \u0915\u093e \u0938\u0947\u0924\u0941\u0964\u0964 \u091c\u0902\u0917\u0932 \u091c\u0902\u0917\u0932 \u0906\u091c \u092d\u0940, \u0928\u093e\u091a \u0930\u0939\u0947 \u0939\u0948\u0902 \u092e\u094b\u0930\u0964 \u0932\u0947\u0915\u093f\u0928 \u092c\u0938\u094d\u0924\u0940 \u092e\u0947\u0902 \u092e\u093f\u0932\u0947, \u0918\u0930 \u0918\u0930 \u0906\u0926\u092e\u0916\u094b\u0930\u0964\u0964 \u0939\u0930 \u0915\u094b\u0908 \u0939\u092e\u0915\u094b \u092e\u093f\u0932\u093e, \u092a\u0939\u0928\u0947 \u0939\u0941\u090f \u0928\u0915\u093e\u092c\u0964 \u0915\u093f\u0938\u0915\u094b \u0905\u092c \u0905\u091a\u094d\u091b\u093e \u0915\u0939\u0947\u0902, \u0915\u093f\u0938\u0915\u094b \u0915\u0939\u0947\u0902 \u0916\u0930\u093e\u092c\u0964\u0964 \u0938\u0941\u0916 \u0938\u0941\u0935\u093f\u0927\u093e \u0915\u0947 \u0915\u0930 \u0932\u093f\u092f\u0947, \u091c\u092e\u093e \u0938\u092d\u0940 \u0938\u093e\u092e\u093e\u0928\u0964 \u0915\u094c\u0921\u093c\u0940 \u092a\u093e\u0938 \u0928 \u092a\u094d\u0930\u0947\u092e \u0915\u0940, \u092c\u0928\u0924\u0947 \u0939\u0948 \u0927\u0928\u0935\u093e\u0928 \u0964\u0964 \u091a\u093e\u0939\u0947 \u092e\u093e\u0932\u093e\u092e\u093e\u0932 \u0939\u094b \u091a\u093e\u0939\u0947 \u0939\u094b \u0915\u0902\u0917\u093e\u0932 \u0964 \u0939\u0930 \u0915\u094b\u0908 \u0915\u0939\u0924\u093e \u092e\u093f\u0932\u093e, \u0926\u0941\u0928\u093f\u092f\u093e \u0939\u0948 \u091c\u0902\u091c\u093e\u0932\u0964\u0964 \u0930\u093e\u091c\u0928\u0940\u0924\u093f \u0915\u093e \u0935\u094d\u092f\u093e\u0915\u0930\u0923, \u0915\u0941\u0930\u094d\u0938\u0940\u0935\u093e\u0932\u093e \u092a\u093e\u0920\u0964 \u092a\u0922\u093c\u093e \u0930\u0939\u0947 \u0939\u0948\u0902 \u0938\u092c \u0939\u092e\u0947\u0902, \u0938\u094b\u0932\u0939 \u0926\u0942\u0928\u0940 \u0906\u0920\u0964\u0964 \u092e\u0928 \u0938\u0947 \u091c\u094b \u092d\u0940 \u092d\u0947\u0902\u091f \u0926\u0947, \u0909\u0938\u0915\u094b \u0915\u0930\u094b \u0915\u092c\u0942\u0932\u0964 \u0915\u093e\u0901\u091f\u093e \u092e\u093f\u0932\u0947 \u092c\u092c\u0942\u0932 \u0915\u093e, \u092f\u093e \u0917\u0942\u0932\u0930 \u0915\u093e \u092b\u0942\u0932\u0964\u0964 \u0938\u093e\u0917\u0930 \u0938\u0947 \u0930\u0916\u0924\u0940 \u0928\u0939\u0940\u0902, \u0938\u0940\u092a\u0940 \u0915\u094b\u0908 \u0906\u0938\u0964 \u090f\u0915 \u0938\u094d\u0935\u093e\u0924\u0940 \u0915\u0940 \u092c\u0942\u0901\u0926 \u0938\u0947, \u092c\u0941\u091d \u091c\u093e\u0924\u0940 \u0939\u0948 \u092a\u094d\u092f\u093e\u0938\u0964\u0964 \u092e\u0928 \u0938\u0947 \u091c\u094b \u092d\u0940 \u092d\u0947\u0902\u091f \u0926\u0947, \u0909\u0938\u0915\u094b \u0915\u0930\u094b \u0915\u093c\u092c\u0942\u0932 \u0964 \u0915\u093e\u0901\u091f\u093e \u092e\u093f\u0932\u0947 \u092c\u092c\u0942\u0932 \u0915\u093e, \u092f\u093e \u0917\u0942\u0932\u0930 \u0915\u093e \u092b\u0942\u0932 \u0964\u0964 \u091c\u093e\u0928\u0947 \u0915\u093f\u0938\u0915\u093e \u0930\u093e\u0938\u094d\u0924\u093e, \u0926\u0947\u0916 \u0930\u0939\u0940 \u0939\u0948 \u091d\u0940\u0932 \u0964 \u0930\u0935\u093e\u091c\u093c\u0947 \u092a\u0930 \u091f\u093e\u0901\u0917 \u0915\u0930, \u091a\u0902\u0926\u093e \u0915\u0940 \u0915\u0902\u0921\u0940\u0932 \u0964\u0964 \u0930\u093e\u0924\u094b\u0902 \u0915\u094b \u0926\u093f\u0928 \u0915\u0939 \u0930\u0939\u093e, \u0926\u093f\u0928 \u0915\u094b \u0915\u0939\u0924\u093e \u0930\u093e\u0924 \u0964\u0964 \u091c\u093f\u0924\u0928\u093e \u090a\u0901\u091a\u093e \u0906\u0926\u092e\u0940, \u0909\u0924\u0928\u0940 \u0928\u0940\u0902\u091a\u0940 \u092c\u093e\u0924 \u0964\u0964 \u092f\u093e \u092f\u0947 \u0909\u0938\u0915\u0940 \u0938\u094c\u0924 \u0939\u0948, \u092f\u093e \u0935\u094b \u0907\u0938\u0915\u0940 \u0938\u094c\u0924 \u0964 \u0907\u0938 \u0915\u0930\u0935\u091f \u0939\u0948 \u091c\u093c\u093f\u0928\u094d\u0926\u0917\u0940, \u0909\u0938 \u0915\u0930\u0935\u091f \u0939\u0948 \u092e\u094c\u0924 \u0964\u0964 \u0938\u0942\u0930\u091c \u0930\u0939\u0924\u0947 '\u0915\u093c\u092e\u094d\u092c\u0930\u0940', \u0915\u0930\u0932\u094b \u092a\u0941\u0930\u0947 \u0915\u093e\u092e \u0964 \u0935\u0930\u0928\u093e \u0925\u094b\u0921\u093c\u0940 \u0926\u0947\u0930 \u092e\u0947\u0902, \u0939\u094b \u091c\u093e\u092f\u0947\u0917\u0940 \u0936\u093e\u092e \u0964\u0964 \u0924\u0941\u092e \u0924\u094b \u0918\u0930 \u0906\u092f\u0947 \u0928\u0939\u0940\u0902, \u0915\u094d\u092f\u094b\u0902 \u0906\u0908 \u092c\u0930\u0938\u093e\u0924 \u0964 \u092c\u093e\u0926\u0932 \u092c\u0930\u0938\u0947 \u0926\u094b \u0918\u0921\u093c\u0940, \u0906\u0901\u0916\u0947\u0902 \u0938\u093e\u0930\u0940 \u0930\u093e\u0924 \u0964\u0964 \u091b\u093e\u092f\u0947 \u092c\u093e\u0926\u0932 \u0926\u0947\u0916\u0915\u0930, \u0916\u0941\u0936 \u0924\u094b \u0939\u0941\u092f\u0947 \u0915\u093f\u0938\u093e\u0928 \u0964 \u0932\u0947\u0915\u093f\u0928 \u092c\u0930\u0938\u0947 \u0907\u0938 \u0915\u093c\u0926\u0930, \u0921\u0942\u092c\u0947 \u0916\u0947\u0924 \u092e\u0915\u093e\u0928 \u0964\u0964 \u091b\u0942\u091f \u0917\u092f\u093e \u092b\u0941\u091f\u092a\u093e\u0925 \u092d\u0940, \u0909\u0938 \u092a\u0930 \u0939\u0948 \u092c\u0930\u0938\u093e\u0924 \u0964 \u0918\u0930 \u0915\u093e \u092e\u0941\u0916\u093f\u092f\u093e \u0938\u094b\u091a\u0924\u093e, \u0915\u0939\u093e\u0901 \u092c\u093f\u0924\u093e\u092f\u0947\u0902 \u0930\u093e\u0924  \u0964\u0964 \u0915\u094b\u0908 \u0915\u091c\u0930\u0940 \u0917\u093e \u0930\u0939\u093e, \u0915\u094b\u0908 \u0917\u093e\u092f\u0947 \u092b\u093e\u0917 \u0964 \u0905\u092a\u0928\u0940-\u0905\u092a\u0928\u0940 \u0922\u092a\u0932\u093f\u092f\u093e\u0901, \u0905\u092a\u0928\u093e-\u0905\u092a\u0928\u093e \u0930\u093e\u0917 \u0964\u0964 \u092a\u0939\u0932\u0947 \u0906\u092a \u092c\u0941\u091d\u093e\u0907\u092f\u0947, \u0905\u092a\u0928\u0947 \u092e\u0928 \u0915\u0940 \u0906\u0917 \u0964 \u092b\u093f\u0930 \u092c\u0938\u094d\u0924\u0940 \u092e\u0947\u0902 \u0917\u093e\u0907\u092f\u0947, \u092e\u0947\u0918 \u092e\u0932\u094d\u0939\u093e\u0930\u0940 \u0930\u093e\u0917 \u0964\u0964 \u0938\u0942\u0930\u091c \u092c\u094b\u0932\u093e \u091a\u093e\u0901\u0926 \u0938\u0947, \u0915\u092d\u0940 \u0915\u093f\u092f\u093e \u0939\u0948 \u0917\u093c\u094c\u0930 \u0964 \u0924\u0947\u0930\u093e \u091c\u0932\u0928\u093e \u0914\u0930 \u0939\u0948, \u092e\u0947\u0930\u093e \u091c\u0932\u0928\u093e \u0914\u0930 \u0964\u0964 \u091c\u092c \u0924\u0915 \u0905\u091a\u094d\u091b\u093e \u092d\u093e\u0917\u094d\u092f \u0939\u0948, \u0922\u0915\u0947 \u0939\u0941\u092f\u0947 \u0939\u0948 \u092a\u093e\u092a \u0964 \u092d\u0947\u0926 \u0916\u0941\u0932\u093e \u0939\u094b \u091c\u093e\u092f\u0947\u0902\u0917\u0947, \u092a\u0932 \u092e\u0947\u0902 \u0928\u0902\u0917\u0947 \u0906\u092a \u0964\u0964 \u092c\u0939\u0941\u0926\u093e \u091b\u094b\u091f\u0940 \u0935\u0938\u094d\u0924\u0941 \u092d\u0940, \u0938\u0902\u0915\u091f \u0915\u093e \u0939\u0932 \u0939\u094b\u092f \u0964 \u0921\u0942\u092c\u0928 \u0939\u093e\u0930\u0947 \u0915\u0947 \u0932\u093f\u092f\u0947, \u0924\u093f\u0928\u0915\u093e \u0938\u092e\u094d\u092c\u0932 \u0939\u094b\u092f \u0964\u0964 ",
                    source:
                        "http://kavitakosh.org/kk/%E0%A4%95%E0%A4%B2%E0%A4%9C%E0%A5%81%E0%A4%97%E0%A5%80_%E0%A4%A6%E0%A5%8B%E0%A4%B9%E0%A5%87_/_%E0%A4%85%E0%A4%82%E0%A4%B8%E0%A4%BE%E0%A4%B0_%E0%A4%95%E0%A4%AE%E0%A5%8D%E0%A4%AC%E0%A4%B0%E0%A5%80",
                    tags: ["\u0915\u0935\u093f\u0924\u093e"],
                    title:
                        "\u0915\u0932\u091c\u0941\u0917\u0940 \u0926\u094b\u0939\u0947"
                },
                {
                    _id: {
                        $oid: "5a53055274ad350ba00ae681"
                    },
                    authorName:
                        " \u0905\u0902\u0938\u093e\u0930 \u0915\u092e\u094d\u092c\u0930\u0940",
                    content:
                        "\u092e\u0947\u0930\u093e \u0909\u0938\u0915\u093e \u092a\u0930\u093f\u091a\u092f \u0907\u0924\u0928\u093e \n\u0935\u094b \u0928\u0926\u093f\u092f\u093e \u0939\u0948, \u092e\u0948\u0902 \u092e\u0930\u0941\u0925\u0932 \u0939\u0942\u0901\u0964\n\n\u0909\u0938\u0915\u0940 \u0938\u0940\u092e\u093e \u0938\u093e\u0917\u0930 \u0924\u0915 \u0939\u0948 \n\u092e\u0947\u0930\u093e \u0915\u094b\u0908 \u091b\u094b\u0930 \u0928\u0939\u0940\u0902 \u0939\u0948\u0964\n\u092e\u0947\u0930\u0940 \u092a\u094d\u092f\u093e\u0938 \u091a\u0941\u0930\u093e \u0932\u0947 \u091c\u093e\u090f \n\u0910\u0938\u093e \u0915\u094b\u0908 \u091a\u094b\u0930 \u0928\u0939\u0940\u0902 \u0939\u0948\u0964\n\n\u092e\u0947\u0930\u093e \u0909\u0938\u0915\u093e \u0907\u0924\u0928\u093e \u0928\u093e\u0924\u093e \n\u0935\u094b \u0916\u093c\u0941\u0936\u092c\u0942 \u0939\u0948, \u092e\u0948\u0902 \u0938\u0902\u0926\u0932 \u0939\u0942\u0901\u0964\n\n\u0909\u0938 \u092a\u0930 \u0924\u0948\u0930\u0947\u0902 \u0926\u0940\u092a \u0936\u093f\u0916\u093e\u090f\u0901\n\u0938\u0942\u0928\u0940 \u0938\u0942\u0928\u0940 \u092e\u0947\u0930\u0940 \u0930\u093e\u0939\u0947\u0902\u0964\n\u0909\u0938\u0915\u0947 \u0924\u091f \u092a\u0930 \u092d\u0940\u0921\u093c \u0932\u0917\u0940 \u0939\u0948 \n\u0915\u094c\u0928 \u0915\u0930\u0947\u0917\u093e \u092e\u0941\u091d\u0938\u0947 \u092c\u093e\u0924\u0947\u0902\u0964\n\n\u092e\u0947\u0930\u093e \u0909\u0938\u0915\u093e \u0905\u0902\u0924\u0930 \u0907\u0924\u0928\u093e \n\u0935\u094b \u092c\u0938\u094d\u0924\u0940 \u0939\u0948, \u092e\u0948\u0902 \u091c\u0902\u0917\u0932 \u0939\u0942\u0901\u0964\n\n\u0909\u0938\u092e\u0947\u0902 \u090f\u0915 \u0928\u093f\u0930\u0928\u094d\u0924\u0930\u0924\u093e \u0939\u0948 \n\u092e\u0948\u0902 \u0924\u094b \u0938\u094d\u0925\u093f\u0930 \u0939\u0942\u0901 \u091c\u0928\u092e \u091c\u0928\u092e \u0938\u0947\u0964 \n\u0935\u094b \u0939\u0948 \u0938\u093e\u0925 \u0938\u093e\u0925 \u090b\u0924\u0941\u0913\u0902 \u0915\u0947 \n\u092e\u